import React, { useState, useEffect } from 'react';
import {Text, View, TouchableOpacity, TextInput, ScrollView} from 'react-native';
import estilop from "./estilop";
import { MaterialIcons } from '@expo/vector-icons';
import { FontAwesome5 } from '@expo/vector-icons';
import { LinearGradient } from 'expo-linear-gradient';
import { Historia } from '../../firebase/historia';

function Photo({navigation , route}) {

    const [p , setp] = useState({});
    const {operacao, setOperacao} = route.params;

    const historia = new Historia();

    useEffect(() => {
        setp(route.params.p);
    }, [route.params.p]);

    const voltar = () => {
        navigation.navigate('Colecao')
    }

    const salvar = (p) => {
        operacao == 'adicionar' ? historia.adicionarTitulo(p) : historia.editarTitulo(p);
        voltar();
    }

    const remover = (p) => {
        historia.removerTitulo(p);
        voltar();
    }


    return (
    <View style={estilop.container}>

        <View style={estilop.header}>
            <TouchableOpacity onPress={voltar}>
            <MaterialIcons name="arrow-back" size={24} color="white"/>
            </TouchableOpacity>
            <Text style={estilop.texto}>p</Text>
        </View>

        <View style={estilop.formularioContainer}>

            <View style={estilop.campoContainer}>
                <FontAwesome5 name="book" size={26} color="white" />
                <TextInput 
                style={estilop.campo}
                placeholder="Titulo"
                placeholderTextColor="#FFF"
                onChangeText={titulo => setp({...p, titulo})}
                value={p.titulo}
                />
            </View>

            <View style={estilop.campoContainer}>
                <FontAwesome5 name="book-reader" size={26} color="white" />
                <TextInput 
                style={estilop.campo}
                placeholder="Competição"
                placeholderTextColor="#FFF"
                onChangeText={competicao => setp({...p, competicao})}
                value={p.competicao}
                />
            </View>

            <View style={estilop.campoContainer}>
                <FontAwesome5 name="calendar-alt" size={26} color="white" />
                <TextInput 
                style={estilop.campo}
                placeholder="Ano da Conquista"
                placeholderTextColor="#FFF"
                keyboardType='numeric'
                maxLength={4}
                onChangeText={AnoConquista => setp({...p, AnoConquista})}
                value={p.AnoConquista ? p.AnoConquista.toString() : p.AnoConquista}
                />
            </View>

            <View style={estilop.campoContainerDescricao}>
                <FontAwesome5 name="book-open" size={26} color="white" />
                <ScrollView style={estilop.campoDescricaoScroll}>
                    <TextInput
                    style={estilop.campoDescricao}
                    placeholder="Descrição"
                    placeholderTextColor="#FFF"
                    multiline={true}
                    numberOfLines={4}
                    blurOnSubmit={false}
                    onChangeText={descricao =>setp({...p, descricao})}
                    value={p.descricao}
                    />
                </ScrollView>
            </View>


            <View style={estilop.botoesContainer}>


                <TouchableOpacity onPress={() => salvar(p)} style={estilop.botaoContainer}>
                    <LinearGradient colors={['#FFFFFF', '#FF0000', '#000000']} style={estilop.botao}>
                        <MaterialIcons name="save" size={28} color="white" />
                    </LinearGradient>

                </TouchableOpacity>

                <TouchableOpacity onPress={() => remover(p)} style={estilop.botaoContainer}>
                    <LinearGradient colors={['#FFFFFF', '#FF0000', '#000000']} style={estilop.botao}>
                        <MaterialIcons name="delete" size={28} color="white" />
                    </LinearGradient>

                </TouchableOpacity>

            </View>


        </View>

    </View>
) 

}
export default Photo;
