import React, { useState, useEffect } from 'react';
import {Text, View, TouchableOpacity, TextInput, ScrollView} from 'react-native';
import estiloPhoto from "./estiloPhoto";
import { MaterialIcons } from '@expo/vector-icons';
import { FontAwesome5 } from '@expo/vector-icons';
import { LinearGradient } from 'expo-linear-gradient';
import { Historia } from '../../firebase/historia';

function Photo({navigation , route}) {

    const [Photo , setPhoto] = useState({});
    const {operacao, setOperacao} = route.params;

    const historia = new Historia();

    useEffect(() => {
        setPhoto(route.params.Photo);
    }, [route.params.Photo]);

    const voltar = () => {
        navigation.navigate('Colecao')
    }

    const salvar = (Photo) => {
        operacao == 'adicionar' ? historia.adicionarTitulo(Photo) : historia.editarTitulo(Photo);
        voltar();
    }

    const remover = (Photo) => {
        historia.removerTitulo(Photo);
        voltar();
    }


    return (
    <View style={estiloPhoto.container}>

        <View style={estiloPhoto.header}>
            <TouchableOpacity onPress={voltar}>
            <MaterialIcons name="arrow-back" size={24} color="white"/>
            </TouchableOpacity>
            <Text style={estiloPhoto.texto}>Photo</Text>
        </View>

    </View>
) 

}
export default Photo;
